import * as actionTypes from "./constant";

const initialStateAdd = {
  loading: false,
  data: null,
  error: null,
};

export const addMovieReducer = (state = initialStateAdd, { payload, type }) => {
  switch (type) {
    case actionTypes.ADD_MOVIE_REQUEST:
      state.loading = true;
      state.data = null;
      state.error = null;
      return { ...state };
    case actionTypes.ADD_MOVIE_SUCCESS:
      state.loading = false;
      state.data = payload;
      state.error = null;
      return { ...state };
    case actionTypes.ADD_MOVIE_FAILED:
      state.loading = false;
      state.data = null;
      state.error = payload;
      return { ...state };
    default:
      return { ...state };
  }
};
