import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { actGetCinemaListApi } from "./modules/action";
import { makeStyles } from "@material-ui/core/styles";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import Container from "@material-ui/core/Container";
import Avatar from "@material-ui/core/Avatar";

function TabPanel(props) {
  const { children, valueLogo, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={valueLogo !== index}
      id={`vertical-tabpanel-${index}`}
      aria-labelledby={`vertical-tab-${index}`}
      {...other}
    >
      {valueLogo === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
    display: "flex",
  },
  tabs: {
    borderRight: `1px solid ${theme.palette.divider}`,
    border: "1px solid #ebebec",
  },
  cinemaLogoTab: {
    padding: 20,
    minWidth: "unset",
  },
  cinemaLogoAvatar: {
    width: 50,
    height: 50,
  },
}));

function CinemaList() {
  const dispatch = useDispatch();
  const cinemaList = useSelector((state) => state.cinemaListReducer);

  useEffect(() => {
    dispatch(actGetCinemaListApi());
  }, []);

  const classes = useStyles();
  const [valueLogo, setValueLogo] = useState(0);

  const handleChangeLogo = (event, newValue) => {
    setValueLogo(newValue);
  };

  const renderCinemaLogo = (arrData) => {
    return arrData.map((item, index) => {
      return (
        <Tab
          className={classes.cinemaLogoTab}
          key={item.biDanh}
          icon={
            <Avatar
              alt={item.biDanh}
              src={item.logo}
              className={classes.cinemaLogoAvatar}
            />
          }
        />
      );
    });
  };

  return (
    <Container maxWidth="md" className={classes.root}>
      <Tabs
        orientation="vertical"
        value={valueLogo}
        onChange={handleChangeLogo}
        className={classes.tabs}
      >
        {cinemaList.data ? renderCinemaLogo(cinemaList.data) : null}
      </Tabs>
      <TabPanel valueLogo={valueLogo} index={0}>
        Item One
      </TabPanel>
      <TabPanel valueLogo={valueLogo} index={1}>
        Item Two
      </TabPanel>
      <TabPanel valueLogo={valueLogo} index={2}>
        Item Three
      </TabPanel>
      <TabPanel valueLogo={valueLogo} index={3}>
        Item Four
      </TabPanel>
      <TabPanel valueLogo={valueLogo} index={4}>
        Item Five
      </TabPanel>
      <TabPanel valueLogo={valueLogo} index={5}>
        Item Six
      </TabPanel>
    </Container>
  );
}

export default CinemaList;
