import { yupResolver } from "@hookform/resolvers/yup";
import {
  FormControl,
  Grid,
  IconButton,
  InputAdornment,
  InputLabel,
  makeStyles,
  Select,
  TextField,
} from "@material-ui/core";
import { Visibility, VisibilityOff } from "@material-ui/icons";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { useDispatch, useSelector } from "react-redux";
import * as yup from "yup";

const schema = yup.object().shape({
  hoTen: yup
    .string()
    .required("Đây là trường bắt buộc !")
    .matches(/^([^0-9]*)$/, "Họ và tên không chứa số !"),
  taiKhoan: yup.string().required("Đây là trường bắt buộc !"),

  email: yup.string().required("Đây là trường bắt buộc !"),
  matKhau: yup
    .string()
    .required("Đây là trường bắt buộc !")
    .min(6, "Mật khẩu phải có ít nhất 6 kí tự !"),
  soDt: yup
    .number()
    .required("Đây là trường bắt buộc !")
    .typeError("Vui lòng nhập số điện thoại"),
});

const useStyles = makeStyles((theme) => ({
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  formControl: {
    width: "100%",
  },
  button: {
    marginRight: "8px",
  },
}));

export default function Account() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const { register, handleSubmit, errors } = useForm({
    mode: "onBlur",
    resolver: yupResolver(schema),
  });

  const [showPassword, setShowPassword] = useState(false);

  const currentUser = useSelector((state) => state.currentUserReducer);

  const [user, setUser] = useState(
    JSON.parse(localStorage.getItem("currentUser"))
  );

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;

    setUser({
      ...user,
      [name]: value,
    });
  };

  const handleClickShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  return (
    <form className={classes.form} noValidate>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            variant="outlined"
            required
            fullWidth
            id="taiKhoan"
            label="Tài Khoản"
            name="taiKhoan"
            autoComplete="taiKhoan"
            inputRef={register}
            error={!!errors.taiKhoan}
            helperText={errors?.taiKhoan?.message}
            value={user.taiKhoan}
            disabled={true}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            variant="outlined"
            required
            fullWidth
            name="matKhau"
            label="Mật Khẩu"
            type="password"
            id="matKhau"
            autoComplete="current-password"
            inputRef={register}
            error={!!errors.matKhau}
            helperText={errors?.matKhau?.message}
            type={showPassword ? "text" : "password"}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                    edge="end"
                  >
                    {showPassword ? <Visibility /> : <VisibilityOff />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
            value={user.matKhau}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            variant="outlined"
            required
            fullWidth
            id="hoTen"
            label="Họ Tên"
            name="hoTen"
            autoComplete="hoTen"
            inputRef={register}
            error={!!errors.hoTen}
            helperText={errors?.hoTen?.message}
            value={user.hoTen}
            onChange={handleChange}
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            variant="outlined"
            required
            fullWidth
            id="email"
            label="Email"
            name="email"
            autoComplete="email"
            inputRef={register}
            error={!!errors.email}
            helperText={errors?.email?.message}
            value={user.email}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            variant="outlined"
            required
            fullWidth
            id="soDt"
            label="Số điện thoại"
            name="soDt"
            autoComplete="soDt"
            inputRef={register}
            error={!!errors.soDt}
            helperText={errors?.soDt?.message}
            value={user.soDt}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12}>
          <FormControl variant="outlined" className={classes.formControl}>
            <InputLabel htmlFor="outlined-age-native-simple">
              Mã Loại Người Dùng
            </InputLabel>
            <Select
              native
              value={user.maLoaiNguoiDung}
              onChange={handleChange}
              label="Mã Loại Người Dùng"
              inputProps={{
                name: "maLoaiNguoiDung",
                id: "outlined-age-native-simple",
              }}
              inputRef={register}
            >
              <option value={"KhachHang"}>Khách Hàng</option>
              <option value={"QuanTri"}>Quản Trị</option>
            </Select>
          </FormControl>
        </Grid>
      </Grid>
    </form>
  );
}
