import { Grid, makeStyles, TextField } from "@material-ui/core";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import MuiDialogContent from "@material-ui/core/DialogContent";
import MuiDialogActions from "@material-ui/core/DialogActions";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Typography from "@material-ui/core/Typography";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { useDispatch } from "react-redux";
import moment from "moment";
import { useEffect, useState } from "react";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/date-fns";
import axios from "axios";
import { swalFailed, swalSuccess } from "../../../../utils";
import { actGetMoviePagingApi } from "../modules/action";

const schema = yup.object().shape({
  tenPhim: yup.string().required("Đây là trường bắt buộc"),
  biDanh: yup.string().required("Đây là trường bắt buộc"),
  trailer: yup.string().required("Đây là trường bắt buộc"),
  moTa: yup.string().required("Đây là trường bắt buộc"),
  ngayKhoiChieu: yup.required("Đây là trường bắt buộc"),
  danhGia: yup
    .number()
    .required("Đây là trường bắt buộc !")
    .typeError("Vui lòng nhập điểm đánh giá"),
});

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  formControl: {
    width: "100%",
  },
  button: {
    marginRight: "8px",
  },
  formContainer: {
    backgroundColor: theme.palette.common.white,
    borderRadius: "4px",
  },
  dateTimeContainer: {
    display: "flex",
    justifyContent: "space-around",
  },
}));

const styles = (theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(2),
  },
  closeButton: {
    position: "absolute",
    right: theme.spacing(1),
    top: theme.spacing(1),
    color: theme.palette.grey[500],
  },
});

const DialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;
  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton
          aria-label="close"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
  },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(1),
  },
}))(MuiDialogActions);

export default function ManageMovieDialog(props) {
  const classes = useStyles();
  const dispatch = useDispatch();
  const {
    openDialog,
    setOpenDialog,
    handleCloseDialog,
    modal,
    search,
    page,
    numberElementOfPage,
  } = props;

  const [movie, setMovie] = useState({ ...props.movie });
  const [showPassword, setShowPassword] = useState(false);
  const [selectedDate, setSelectedDate] = useState(
    moment(Date()).format("DD-MM-YYYY")
  );
  const [selectedImage, setSelectedImage] = useState(
    "https://tix.vn/app/assets/img/default-film.webp"
  );
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    setMovie(props.movie);
  }, [props.movie]);

  const accessToken = JSON.parse(
    localStorage.getItem("currentUser")
  ).accessToken;

  const handleClickShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setMovie({
      ...movie,
      [name]: value,
    });
  };

  const fileSelectedHandler = (event) => {
    setSelectedFile(event.target.files[0]);
    setSelectedImage(URL.createObjectURL(event.target.files[0]));
    setMovie({
      ...movie,
      ["hinhAnh"]: event.target.files[0].name,
    });
  };

  const handleDateChange = (date) => {
    setMovie({
      ...movie,
      ["ngayKhoiChieu"]: moment(date).format("DD-MM-YYYY"),
    });
    setSelectedDate(date);
  };

  const onAddSubmit = () => {
    movie["maPhim"] = "0";
    axios({
      url: `https://movie0706.cybersoft.edu.vn/api/QuanLyPhim/ThemPhim`,
      method: "POST",
      data: movie,
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    })
      .then((result) => {
        setOpenDialog(false);
        swalSuccess("Thêm thành công").then(() => {
          {
            search.trim() === ""
              ? dispatch(
                  actGetMoviePagingApi(page.toString(), numberElementOfPage)
                )
              : dispatch(
                  actGetMoviePagingApi(
                    page.toString(),
                    numberElementOfPage,
                    search
                  )
                );
          }
        });
        const frm = new FormData();
        frm.append("File", selectedFile, selectedFile.name);
        frm.append("tenPhim", movie.tenPhim);
        frm.append("maNhom", "GP09");
        axios({
          url: "https://movie0706.cybersoft.edu.vn/api/QuanLyPhim/UploadHinhAnhPhim",
          method: "POST",
          data: frm,
        })
          .then((result) => {
            console.log("Them hinh", result);
          })
          .catch((error) => {
            setOpenDialog(false);
            swalFailed(error).then(() => {
              {
                search.trim() === ""
                  ? dispatch(
                      actGetMoviePagingApi(page.toString(), numberElementOfPage)
                    )
                  : dispatch(
                      actGetMoviePagingApi(
                        page.toString(),
                        numberElementOfPage,
                        search
                      )
                    );
              }
            });
          });
      })
      .catch((error) => {
        setOpenDialog(false);
        swalFailed(error);
      });
  };

  const onUpdateSubmit = () => {
    console.log(movie);
    axios({
      url: `https://movie0706.cybersoft.edu.vn/api/QuanLyPhim/CapNhatPhim`,
      method: "POST",
      data: movie,
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    })
      .then((result) => {
