import React, { useState, useEffect } from "react";
import { useStyles } from "./style";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Grid from "@material-ui/core/Grid";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import { headData } from "./data";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import { Drawer, useTheme } from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu";
import Logo from "../Logo";
import MyTypography from "./MyTypography";
import MyGridContainer from "./MyGridContainer";
import TopBarItems from "./TopBarItems";

// Start here
const TopBar = (Component) => {
  const classes = useStyles();
  const theme = useTheme();
  const isMatch = useMediaQuery(theme.breakpoints.down("md"));
  const [open, setOpen] = useState(false);

  const handleDrawer = () => {
    setOpen(true);
  };
  const renderHeader = () => {
    return (
      <>
        {/* Middle */}
        <MyGridContainer className={classes.headData} item lg={8}>
          <TopBarItems className={classes.headDataItems} />
        </MyGridContainer>
        {/* Login/ Sign in */}
        <MyGridContainer item lg={3} spacing={2}>
          <MyGridContainer
            justify="flex-end"
            className={classes.headAuthItems}
            item
            lg={6}
          >
            <AccountCircleIcon fontSize="large" className={classes.authIcon} />
            <MyTypography>Đăng Nhập</MyTypography>
          </MyGridContainer>

          <MyGridContainer
            justify="flex-start"
            className={classes.headAuthItems}
            item
            lg={6}
          >
            <AccountCircleIcon fontSize="large" className={classes.authIcon} />
            <MyTypography>Đăng Ký</MyTypography>
          </MyGridContainer>
        </MyGridContainer>
      </>
    );
  };

  return (
    <div className={classes.root}>
      <AppBar position="static" color="transparent">
        <Toolbar>
          <MyGridContainer justify="flex-start" spacing={2}>
            {/* Logo */}
            <Grid item xs={6} lg={1}>
              <Logo className={classes.logo} />
            </Grid>
            {isMatch ? (
              <Grid item container justify="flex-end" xs={6}>
                <MenuIcon
                  className={classes.menuIcon}
                  color="primary"
                  fontSize="large"
                  onClick={handleDrawer}
                />
              </Grid>
            ) : (
              renderHeader(Component)
            )}
          </MyGridContainer>
        </Toolbar>
      </AppBar>

      <Drawer
        anchor="left"
        open={open}
        onClose={() => {
          setOpen(false);
        }}
      >
        {renderHeader(Component)}
      </Drawer>
    </div>
  );
};

export default TopBar;
