import React, { useEffect, useState } from "react";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import EditIcon from "@material-ui/icons/Edit";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import { useDispatch, useSelector } from "react-redux";
import { actDeleteUserApi, actGetUserPagingApi } from "./modules/action";
import { Pagination } from "@material-ui/lab";
import { InputBase, Typography } from "@material-ui/core";
import SearchIcon from "@material-ui/icons/Search";
import Dialog from "@material-ui/core/Dialog";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import MuiDialogContent from "@material-ui/core/DialogContent";
import MuiDialogActions from "@material-ui/core/DialogActions";
const useStyles = makeStyles((theme) => ({
  root: {
    "& > * + *": {
      marginTop: theme.spacing(2),
    },
  },
  table: {
    minWidth: 650,
  },
  paper: {
    padding: "2px 4px",
    display: "flex",
    alignItems: "center",
    margin: "auto",
    width: "400px",
  },
  inputBase: {
    marginLeft: theme.spacing(1),
    flex: 1,
  },
  rootDialog: {
    margin: 0,
    padding: theme.spacing(2),
  },
  closeButton: {
    position: "absolute",
    right: theme.spacing(1),
    top: theme.spacing(1),
    color: theme.palette.grey[500],
  },
}));

const DialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;
  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton
          aria-label="close"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
  },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(1),
  },
}))(MuiDialogActions);

export default function ManageUser() {
  {
    console.log(" list render");
  }
  const classes = useStyles();
  const dispatch = useDispatch();
  const userPaging = useSelector((state) => state.userPagingReducer);

  const [page, setPage] = useState(1);
  const [search, setSearch] = useState("");

  useEffect(() => {
    dispatch(actGetUserPagingApi(page.toString(), "8"));
  }, []);

  //Pagination
  const handleChange = (event, value) => {
    setPage(value);
    {
      search === ""
        ? dispatch(actGetUserPagingApi(value.toString(), "8"))
        : dispatch(actGetUserPagingApi(value.toString(), "8", search));
    }
  };

  // search box
  const handleOnChangeSearch = (e) => {
    setSearch(e.target.value);
  };

  // find user
  const handleOnClickSearch = () => {
    setPage(1);
    {
      search.trim() === ""
        ? dispatch(actGetUserPagingApi("1", "8"))
        : dispatch(actGetUserPagingApi("1", "8", search));
    }
  };
  const handleOnKeyPress = (e) => {
    if (e.keyCode == 13) {
      e.preventDefault();
      setPage(1);
      {
        search.trim() === ""
          ? dispatch(actGetUserPagingApi("1", "8"))
          : dispatch(actGetUserPagingApi("1", "8", search));
      }
    }
  };

  async function deleteUserAsync(taiKhoan) {
    try {
      await dispatch(actDeleteUserApi(taiKhoan));
      if (search.trim() === "") {
        dispatch(actGetUserPagingApi(page.toString(), "8"));
      }
      dispatch(actGetUserPagingApi(page.toString(), "8", search));
    } catch (error) {
      console.log(error);
    }
  }

  const deleteUser = (taiKhoan) => {
    dispatch(actDeleteUserApi(taiKhoan));
    if (search.trim() === "") {
      dispatch(actGetUserPagingApi(page.toString(), "8"));
    }
    dispatch(actGetUserPagingApi(page.toString(), "8", search));
  };

  const renderUserPaging = () => {
    return userPaging.data.items.map((user) => {
      return (
        <TableRow key={user.email}>
          <TableCell component="th" scope="row">
            {user.taiKhoan}
          </TableCell>
          <TableCell align="right">{user.hoTen}</TableCell>
          <TableCell align="right">{user.email}</TableCell>
          <TableCell align="right">{user.soDt}</TableCell>
          <TableCell align="right">
            {user.maLoaiNguoiDung === "KhachHang" ? "Khách Hàng" : "Quản Trị"}
          </TableCell>
          <TableCell align="right">
            <IconButton aria-label="edit" color="secondary">
              <EditIcon />
            </IconButton>
            <IconButton
              aria-label="delete"
              color="primary"
              onClick={() => deleteUserAsync(user.taiKhoan)}
            >
              <DeleteIcon />
            </IconButton>
          </TableCell>
        </TableRow>
      );
    });
  };

  return (
    <div>
      <Paper elevation={4} component="form" className={classes.paper}>
        <InputBase
          placeholder="Tìm kiếm người dùng"
          className={classes.inputBase}
          onChange={handleOnChangeSearch}
          onKeyDown={handleOnKeyPress}
        />
        <IconButton onClick={handleOnClickSearch}>
          <SearchIcon color="primary" />
        </IconButton>
      </Paper>

      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Tài Khoản</TableCell>
              <TableCell align="right">Họ Tên</TableCell>
              <TableCell align="right">Email</TableCell>
              <TableCell align="right">Số Điện Thoại</TableCell>
              <TableCell align="right">Loại Người Dùng</TableCell>
              <TableCell align="right">Chức năng</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>{userPaging.data ? renderUserPaging() : null}</TableBody>
        </Table>
      </TableContainer>
      {userPaging.data ? (
        <div className={classes.root}>
          <Pagination
            count={userPaging.data.totalPages - 1}
            page={page}
            onChange={handleChange}
          />
        </div>
      ) : null}
    </div>
  );
}
