import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import EditIcon from "@material-ui/icons/Edit";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import { useDispatch, useSelector } from "react-redux";
import { actDeleteUserApi, actGetUserPagingApi } from "./modules/action";
import { Pagination } from "@material-ui/lab";
import { Button, InputBase } from "@material-ui/core";
import SearchIcon from "@material-ui/icons/Search";
import ManageDialog from "./ManageDialog";
import axios from "axios";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > * + *": {
      marginTop: theme.spacing(2),
    },
  },
  table: {
    minWidth: 650,
  },
  paper: {
    padding: "2px 4px",
    display: "flex",
    alignItems: "center",
    margin: "auto",
    width: "400px",
  },
  inputBase: {
    marginLeft: theme.spacing(1),
    flex: 1,
  },
}));

export default function ManageUser() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const userPaging = useSelector((state) => state.userPagingReducer);
  const deleteUserData = useSelector((state) => state.deleteUserReducer.data);
  const [page, setPage] = useState(1);
  const [search, setSearch] = useState("");

  const [openDialog, setOpenDialog] = useState(false);

  const [modal, setModal] = useState({
    title: "Thêm Người Dùng",
    button: "Thêm",
    id: "them",
  });

  const handleClickOpenDialog = () => {
    setOpenDialog(true);
  };
  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const handleUpdate = () => {
    setModal({
      title: "Chỉnh Sửa Người Dùng",
      button: "Cập Nhật",
      id: "sua",
    });
    setOpenDialog(true);
  };

  const handleAdd = () => {
    setModal({
      title: "Thêm Người Dùng",
      button: "Thêm",
      id: "them",
    });
    setOpenDialog(true);
  };

  useEffect(() => {
    dispatch(actGetUserPagingApi(page.toString(), "8"));
  }, []);

  //Pagination
  const handleChange = (event, value) => {
    setPage(value);
    {
      search === ""
        ? dispatch(actGetUserPagingApi(value.toString(), "8"))
        : dispatch(actGetUserPagingApi(value.toString(), "8", search));
    }
  };

  // search box
  const handleOnChangeSearch = (e) => {
    setSearch(e.target.value);
  };

  // find user
  const handleOnClickSearch = () => {
    setPage(1);
    {
      search.trim() === ""
        ? dispatch(actGetUserPagingApi("1", "8"))
        : dispatch(actGetUserPagingApi("1", "8", search));
    }
  };
  const handleOnKeyPress = (e) => {
    if (e.keyCode == 13) {
      e.preventDefault();
      setPage(1);
      {
        search.trim() === ""
          ? dispatch(actGetUserPagingApi("1", "8"))
          : dispatch(actGetUserPagingApi("1", "8", search));
      }
    }
  };

  const deleteUserAsync = (taiKhoan) => {
    dispatch(actDeleteUserApi(taiKhoan));
  };

  const deleteUser = (taiKhoan) => {
    let token = "";
    if (localStorage.getItem("currentUser")) {
      token = JSON.parse(localStorage.getItem("currentUser")).accessToken;
    }

    axios({
      url: `https://movie0706.cybersoft.edu.vn/api/QuanLyNguoiDung/XoaNguoiDung`,
      method: "DELETE",
      params: {
        taiKhoan,
      },
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((result) => {
        console.log(result.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const renderUserPaging = () => {
    return userPaging.data.items.map((user) => {
      return (
        <TableRow key={user.email}>
          <TableCell component="th" scope="row">
            {user.taiKhoan}
          </TableCell>
          <TableCell align="right">{user.hoTen}</TableCell>
          <TableCell align="right">{user.email}</TableCell>
          <TableCell align="right">{user.soDt}</TableCell>
          <TableCell align="right">
            {user.maLoaiNguoiDung === "KhachHang" ? "Khách Hàng" : "Quản Trị"}
          </TableCell>
          <TableCell align="right">
            <IconButton
              aria-label="edit"
              color="secondary"
              onClick={handleUpdate}
            >
              <EditIcon />
            </IconButton>
            <IconButton
              aria-label="delete"
              color="primary"
              onClick={() => deleteUserAsync(user.taiKhoan)}
            >
              <DeleteIcon />
            </IconButton>
          </TableCell>
        </TableRow>
      );
    });
  };

  return (
    <div>
      <Paper elevation={4} component="form" className={classes.paper}>
        <InputBase
          placeholder="Tìm kiếm người dùng"
          className={classes.inputBase}
          onChange={handleOnChangeSearch}
          onKeyDown={handleOnKeyPress}
        />
        <IconButton onClick={handleOnClickSearch}>
          <SearchIcon color="primary" />
        </IconButton>
      </Paper>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Tài Khoản</TableCell>
              <TableCell align="right">Họ Tên</TableCell>
              <TableCell align="right">Email</TableCell>
              <TableCell align="right">Số Điện Thoại</TableCell>
              <TableCell align="right">Loại Người Dùng</TableCell>
              <TableCell align="right">Chức năng</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>{userPaging.data ? renderUserPaging() : null}</TableBody>
        </Table>
      </TableContainer>
      {userPaging.data ? (
        <div className={classes.root}>
          <Pagination
            count={userPaging.data.totalPages - 1}
            page={page}
            onChange={handleChange}
          />
        </div>
      ) : null}

      {/* Dialog */}
      <Button variant="outlined" color="primary" onClick={handleAdd}>
        Thêm
      </Button>
      <ManageDialog
        openDialog={openDialog}
        handleCloseDialog={handleCloseDialog}
        modal={modal}
      />
    </div>
  );
}
