import { makeStyles } from "@material-ui/core";

export const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
    display: "flex",
    height: 90 * 8,
    maxHeight: 90 * 8,
    overflow: "hidden",
    marginBottom: theme.spacing(5),
  },
  tabs: {
    position: "relative",
    width: 90,
    border: `1px solid ${theme.palette.grey[500]}`,
  },
  cinemaLogoTab: {
    padding: 20,
    minWidth: "unset",

    "&:after": {
      content: "''",
      display: "block",
      position: "absolute",
      width: "80%",
      height: 1,
      bottom: 0,
      background: "rgba(238,238,238,.88)",
    },
  },
  cinemaLogoAvatar: {
    width: 50,
    height: 50,
  },
  cinemaTab: {
    position: "relative",
    height: 90,
    textAlign: "left",
    padding: "20px 15px 15px 20px",
    width: 280,

    "&:after": {
      content: "''",
      display: "block",
      position: "absolute",
      width: "80%",
      height: 1,
      bottom: 0,
      background: "rgba(238,238,238,.88)",
    },
  },
  cinemaByIdList: {
    width: 264,
    border: "1px solid #e9e9e9",
  },
  cinemaTenCumRap: {
    display: "-webkit-box",
    WebkitLineClamp: "1",
    WebkitBoxOrient: "vertical",
    textOverflow: "ellipsis",
    overflow: "hidden",

    lineHeight: 1.4,
    color: theme.palette.secondary.light,
    fontWeight: theme.typography.fontWeightMedium,
  },
  cinemaDiaChi: {
    display: "-webkit-box",
    WebkitLineClamp: "1",
    WebkitBoxOrient: "vertical",
    textOverflow: "ellipsis",
    overflow: "hidden",

    color: theme.palette.grey[600],
    fontWeight: theme.typography.fontWeightRegular,
  },
  cinemaChiTiet: {
    textDecoration: "none",
    textTransform: "lowercase",
    fontSize: theme.typography.h6.fontSize,
    color: theme.palette.primary.main,
  },
  cinemaTabPanel: {
    flexGrow: 1,
    overflowY: "scroll",
    width: "540px",
    border: "1px solid #e9e9e9",
  },
  ngayChieu: {
    color: "#108f3e",
    fontSize: "14px",
    fontWeight: 500,
  },
  gioChieu: {
    color: "#fa5238",
    fontSize: "16px",
    fontWeight: 500,
  },
  phimInfo: {
    padding: "0 20px",
    width: "555px",
  },
  ngayGioChieuContainer: {
    display: "flex",
    flexWrap: "wrap",
  },
  ngayGioChieuBox: {
    width: "45%",
    backgroundColor: "rgba(246, 246, 246, 0.5)",
    border: "1px solid #e4e4e4",

    margin: "0 15px 15px 0",
    padding: 10,
    borderRadius: 4,
    color: "#9b9b9b",
    cursor: "pointer",
  },
  ngayGioChieuInfo: {
    display: "flex",
    alignItems: "center",
  },
  tenPhim: {
    fontWeight: 500,
    fontSize: "16px",
    marginBottom: "8px",
  },
  anhPhim: {
    width: 100,
    height: 138,
  },
  movieSingle: {
    padding: 20,
    display: "flex",
  },
}));
