import { Container, Grid } from "@material-ui/core";
import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import MovieSingle from "../MovieSingle";
import { actGetMovieListApi } from "./modules/action";
import Spinner from "react-spinner-material";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Pagination from "@material-ui/lab/Pagination";
import Carousel from "react-material-ui-carousel";
import FiberManualRecordIcon from "@material-ui/icons/FiberManualRecord";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import theme from "../../theme";

const useStyles = makeStyles((theme) => ({
  carousel: {
    position: "relative",
  },
  navButton: {
    fontSize: "50px",
    color: theme.palette.grey[500],
  },
}));

const chunkArray = (myArray, chunk_size) => {
  const tempArray = [];
  for (let i = 0; i < myArray.length; i += chunk_size) {
    myChunk = myArray.slice(i, i + chunk_size);
    tempArray.push(myChunk);
  }
  return tempArary;
};

const renderMovieGroup = (length, movieList) => {
  for (let i = 0; i < length; i++) {
    return <Grid container>{renderMovieList(movieList)}</Grid>;
  }
};

const renderMovieList = (movieList) => {
  return movieList.data.map((item) => {
    return (
      <Grid key={item.maPhim} item={true} xs={3}>
        <MovieSingle data={item} />
      </Grid>
    );
  });
};

const renderLoading = () => {
  return (
    <div
      style={{
        height: "80vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        borderColor: "#0ff",
      }}
    >
      <Spinner
        size={120}
        spinnerColor={"#00f"}
        spinnerWidth={1}
        visible={true}
      />
    </div>
  );
};

function MovieList(props) {
  const movieList = useSelector((state) => state.movieListReducer);
  const dispatch = useDispatch();

  const classes = useStyles();

  useEffect(() => {
    dispatch(actGetMovieListApi());
  }, []);

  return (
    <Container maxWidth="lg">
      {movieList.loading ? renderLoading() : null}
      <Carousel
        className={classes.carousel}
        animation="slide"
        autoPlay={false}
        navButtonsAlwaysVisible={true}
        NextIcon={<ArrowForwardIosIcon className={classes.navButton} />}
        PrevIcon={<ArrowBackIosIcon className={classes.navButton} />}
        navButtonsProps={{
          style: {
            backgroundColor: "transparent",
          },
        }}
        indicatorIconButtonProps={{
          style: {
            padding: "4px",
          },
        }}
        IndicatorIcon={<FiberManualRecordIcon style={{ fontSize: "20px" }} />}
        activeIndicatorIconButtonProps={{
          style: {
            color: theme.palette.primary.main,
          },
        }}
        indicatorContainerProps={{
          style: {
            position: "absolute",
            marginTop: "-5%",
          },
        }}
      >
        {movieList.data ? renderMovieList(movieList) : null}
      </Carousel>
    </Container>
  );
}

export default MovieList;
