import * as actionTypes from "./constant";
import axios from "axios";

export const actAddMovieApi = (movie, selectedFile) => {
  const accessToken = JSON.parse(
    localStorage.getItem("currentUser")
  ).accessToken;
  return (dispatch) => {
    dispatch(actAddMovieRequest());
    axios({
      url: `https://movie0706.cybersoft.edu.vn/api/QuanLyPhim/ThemPhim`,
      method: "POST",
      data: movie,
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    })
      .then((result) => {
        dispatch(actAddMovieSuccess(result.data));

        const frm = new FormData();
        frm.append("File", selectedFile, selectedFile.name);
        frm.append("tenPhim", movie.tenPhim);
        frm.append("maNhom", "GP09");
      })
      .catch((error) => {
        dispatch(actAddMovieFailed(error));
      });
  };
};

const actAddMovieRequest = () => {
  return {
    type: actionTypes.ADD_MOVIE_REQUEST,
  };
};

const actAddMovieSuccess = (movie) => {
  return {
    type: actionTypes.ADD_MOVIE_SUCCESS,
    payload: movie,
  };
};

const actAddMovieFailed = (error) => {
  return {
    type: actionTypes.ADD_MOVIE_FAILED,
    payload: error,
  };
};
