import { makeStyles } from "@material-ui/core";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import MuiDialogContent from "@material-ui/core/DialogContent";
import MuiDialogActions from "@material-ui/core/DialogActions";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Typography from "@material-ui/core/Typography";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { useDispatch } from "react-redux";
import moment from "moment";
import { useEffect, useState } from "react";
const schema = yup.object().shape({});

const useStyles = makeStyles((theme) => ({
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  formControl: {
    width: "100%",
  },
  button: {
    marginRight: "8px",
  },
}));

const styles = (theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(2),
  },
  closeButton: {
    position: "absolute",
    right: theme.spacing(1),
    top: theme.spacing(1),
    color: theme.palette.grey[500],
  },
});

const DialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;
  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton
          aria-label="close"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
  },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(1),
  },
}))(MuiDialogActions);

export default function ManageMovieDialog(props) {
  const classes = useStyles();
  const dispatch = useDispatch();
  const {
    openDialog,
    setOpenDialog,
    handleCloseDialog,
    modal,
    search,
    page,
    numberElementOfPage,
  } = props;

  const [movie, setMovie] = useState({ ...props.modal.movie });
  const [showPassword, setShowPassword] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date("2021-08-29"));
  const [selectedImage, setSelectedImage] = useState(
    "https://tix.vn/app/assets/img/default-film.webp"
  );
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    setMovie(props.modal.movie);
  }, [props.modal.movie]);

  const accessToken = JSON.parse(
    localStorage.getItem("currentUser")
  ).accessToken;

  const { register, handleSubmit, errors } = useForm({
    mode: "onBlur",
    resolver: yupResolver(schema),
  });

  const handleClickShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setMovie({
      ...movie,
      [name]: value,
    });
  };

  const fileSelectedHandler = (event) => {
    setSelectedFile(event.target.files[0]);
    setSelectedImage(URL.createObjectURL(event.target.files[0]));
    setMovie({
      ...movie,
      ["hinhAnh"]: event.target.files[0].name,
    });
  };

  const handleDateChange = (date) => {
    setMovie({
      ...movie,
      ["ngayKhoiChieu"]: moment(date).format("DD-MM-YYYY"),
    });
    setSelectedDate(date);
  };

  return (
    <div>
      <Dialog maxWidth="sm" onClose={handleCloseDialog} open={openDialog}>
        <DialogTitle onClose={handleCloseDialog}>
          <Typography
            color={modal.id === "them" ? "primary" : "secondary"}
            variant="h3"
          >
            {modal.title}
          </Typography>
        </DialogTitle>
        <DialogContent dividers>
          <form className={classes.form} noValidate>
            <Grid className={classes.formContainer} container spacing={3}>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  id="tenPhim"
                  label="Tên phim"
                  name="tenPhim"
                  autoComplete="tenPhim"
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  id="biDanh"
                  label="Bí danh"
                  name="biDanh"
                  autoComplete="biDanh"
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  id="trailer"
                  label="Trailer"
                  name="trailer"
                  autoComplete="trailer"
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <Typography>Hinh Anh</Typography>
                <img
                  alt="upload image"
                  style={{
                    display: "block",
                    height: 100,
                    width: 100,
                  }}
                  src={selectedImage}
                />
                <input
                  accept="image/*"
                  style={{
                    display: "none",
                  }}
                  id="contained-button-file"
                  multiple
                  type="file"
                  onChange={fileSelectedHandler}
                />
                <label htmlFor="contained-button-file">
                  <Button variant="contained" color="primary" component="span">
                    Chọn ảnh
                  </Button>
                </label>
              </Grid>
              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  id="moTa"
                  label="Mô tả"
                  name="moTa"
                  autoComplete="moTa"
                  onChange={handleChange}
                />
              </Grid>

              <Grid item xs={12} className={classes.dateTimeContainer}>
                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                  <KeyboardDatePicker
                    format="MM/dd/yyyy"
                    margin="normal"
                    id="date-picker-inline"
                    label="Date picker inline"
                    value={selectedDate}
                    onChange={handleDateChange}
                    KeyboardButtonProps={{
                      "aria-label": "change date",
                    }}
                  />
                </MuiPickersUtilsProvider>
              </Grid>

              <Grid item xs={12}>
                <TextField
                  variant="outlined"
                  required
                  fullWidth
                  id="danhGia"
                  label="Đánh giá"
                  name="danhGia"
                  autoComplete="danhGia"
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleAddMovie}
                >
                  Thêm
                </Button>
              </Grid>
            </Grid>
          </form>
        </DialogContent>
        <DialogActions>
          <Button
            className={classes.button}
            variant="contained"
            // onClick={
            //   modal.id === "them"
            //     ? handleSubmit(onAddSubmit)
            //     : handleSubmit(onUpdateSubmit)
            // }
            autoFocus
            color={modal.id === "them" ? "primary" : "secondary"}
          >
            {modal.button}
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
