import React from "react";
import MyTypography from "../MyTypography";
import { data } from "./data";
import { Link } from "react-scroll";
import { Link as RouteLink } from "react-router-dom";
import { useLocation, useHistory } from "react-router-dom";
import { useStyles } from "../style";

function TopBarItems(props) {
  const location = useLocation();
  const history = useHistory();
  const classes = useStyles();

  const lastItem = data.length - 1;

  if (localStorage.getItem("currentUser")) {
    if (
      JSON.parse(localStorage.getItem("currentUser")).maLoaiNguoiDung ===
      "QuanTri"
    ) {
      if (data[lastItem].id !== "dashboard") {
        data.push({
          name: "Dashboard",
          id: "dashboard",
        });
      }
    }
    if (data[lastItem].id === "dashboard") {
      data.splice(lastItem, 1);
    }
  }

  const handleClick = () => {
    if (location.pathname !== "/") {
      history.push("/");
    }
  };
  return data.map((item) => {
    if (item.id === "dashboard") {
      return (
        <RouteLink className={classes.headDataItems} to={item.id}>
          <MyTypography>{item.name}</MyTypography>
        </RouteLink>
      );
    }
    return (
      <Link
        onClick={handleClick}
        key={item.id}
        {...props}
        to={item.id}
        smooth={true}
        duration={1000}
        offset={-50}
        spy={true}
      >
        <MyTypography>{item.name}</MyTypography>
      </Link>
    );
  });
}

export default TopBarItems;
