import {
  Button,
  Container,
  Divider,
  FormControl,
  Grid,
  makeStyles,
  NativeSelect,
} from "@material-ui/core";
import React, { useState } from "react";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "block",
    height: theme.spacing(10),
    position: "absolute",
    width: "100%",
    left: "50%",
    bottom: "0",
    transform: "translate(-50%,50%)",
  },
  homeToolContainer: {
    backgroundColor: theme.palette.common.white,
    borderRadius: "5px",
    zIndex: "2",
    boxShadow: "0 0 10px rgb(0 0 0 / 30%)",
    height: "100%",
  },
  nativeSelect: {
    "&:before": {
      content: "unset",
    },
    display: "flex",
    justifyContent: "space-around",

    fontSize: "16px",
    fontWeight: 500,
    padding: "20px",
  },
  gridItem: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-around",
  },
  selectFilm: {
    flexGrow: "1",
  },
  formControl: {
    width: "100%",
  },
}));

export default function HomeTool() {
  const classes = useStyles();

  const [state, setState] = React.useState({
    age: "",
    name: "hai",
  });

  const handleChange = (event) => {
    const name = event.target.name;
    setState({
      ...state,
      [name]: event.target.value,
    });
  };

  return (
    <Container id="homeTool" className={classes.root} maxWidth="md">
      <Grid container className={classes.homeToolContainer}>
        <Grid item xs={4} className={classes.gridItem}>
          <div className={classes.selectFilm}>
            <FormControl className={classes.formControl}>
              <NativeSelect
                value={state.age}
                name="age"
                onChange={handleChange}
                className={classes.nativeSelect}
              >
                <option value="" disabled>
                  Phim
                </option>
                <option value={10}>Ten</option>
                <option value={20}>Twenty</option>
                <option value={30}>Thirty</option>
              </NativeSelect>
            </FormControl>
          </div>
        </Grid>
        <Divider orientation="vertical" flexItem />
        <Grid item xs={2}>
          <div className={classes.selectCinema}></div>
        </Grid>
        <Grid item xs={2}>
          <div className={classes.selectDate}></div>
        </Grid>
        <Grid item xs={2}>
          <div className={classes.selectSession}></div>
        </Grid>
        <Grid item xs={2}>
          <div className={classes.buyContainer}></div>
        </Grid>
      </Grid>
    </Container>
  );
}
