import React, { useState, useEffect } from "react";
import { useStyles } from "./style";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import { headData } from "./data";
import logo from "../../assets/pasgo.jpg";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import { Drawer, useTheme } from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu";

const MyTypography = (props) => {
  return (
    <Typography align="center" variant="h3" {...props}>
      {props.children}
    </Typography>
  );
};

const MyGridContainer = (props) => {
  return (
    <Grid container justify="center" alignItems="center" {...props}>
      {props.children}
    </Grid>
  );
};

// Start here
const TopBar = () => {
  const classes = useStyles();
  const theme = useTheme();
  const isMatch = useMediaQuery(theme.breakpoints.down("md"));
  const [open, setOpen] = useState(false);

  const handleDrawer = () => {
    setOpen(true);
  };

  const showHeadData = (data) => {
    return data.map((item) => {
      return (
        <MyTypography key={item.id} className={classes.headDataItems}>
          {item.name}
        </MyTypography>
      );
    });
  };

  return (
    <div className={classes.root}>
      <AppBar position="static" color="transparent">
        <Toolbar>
          <MyGridContainer justify="flex-start" spacing={2}>
            {/* Logo */}
            <Grid item xs={1}>
              <img src={logo} className={classes.logo} />
            </Grid>
            {isMatch ? (
              <MenuIcon fontSize="large" onClick={handleDrawer} />
            ) : (
              <>
                {/* Middle */}
                <MyGridContainer item xs={8}>
                  {showHeadData(headData)}
                </MyGridContainer>
                {/* Login/ Sign in */}
                <MyGridContainer item xs={3} spacing={2}>
                  <MyGridContainer
                    justify="flex-end"
                    className={classes.headAuthItems}
                    item
                    xs={6}
                  >
                    <AccountCircleIcon
                      fontSize="large"
                      className={classes.icon}
                    />
                    <MyTypography>Đăng Nhập</MyTypography>
                  </MyGridContainer>

                  <MyGridContainer
                    justify="flex-start"
                    className={classes.headAuthItems}
                    item
                    xs={6}
                  >
                    <AccountCircleIcon
                      fontSize="large"
                      className={classes.icon}
                    />
                    <MyTypography>Đăng Ký</MyTypography>
                  </MyGridContainer>
                </MyGridContainer>
              </>
            )}
          </MyGridContainer>
        </Toolbar>
      </AppBar>

      <Drawer
        anchor="left"
        open={open}
        onClose={() => {
          setOpen(false);
        }}
      >
        This is drawer
      </Drawer>
    </div>
  );
};

export default TopBar;
