import React, { useState } from "react";
import MyTypography from "../MyTypography";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import { useStyles } from "../style";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";

function TopBarAuth(props) {
  const classes = useStyles();
  const dispatch = useDispatch();

  const currentUser = useSelector((state) => state.currentUserReducer);

  const [anchorEl, setAnchorEl] = useState(null);

  const handleClose = () => {
    setAnchorEl(null);
  };

  const openMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const renderUser = () => {
    return (
      <>
        <Link
          // to="/account"
          justify="flex-end"
          item
          lg={6}
          className={classes.headAuthItems}
          onClick={openMenu}
        >
          <AccountCircleIcon fontSize="large" className={classes.authIcon} />
          <MyTypography className={classes.overflowEllipis}>
            {currentUser.taiKhoan}
          </MyTypography>
        </Link>
        <div>
          <Menu
            id="lame-menu"
            anchorEl={anchorEl}
            keepMounted
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
            <MenuItem onClick={handleClose}>Thông tin</MenuItem>
            <MenuItem onClick={handleClose}>Cài đặt</MenuItem>
            <MenuItem onClick={handleClose}>Đăng xuất</MenuItem>
          </Menu>
        </div>
        <Link
          justify="flex-end"
          item
          lg={6}
          className={classes.headAuthItems}
          onClick={() => {
            localStorage.removeItem("currentUser");
            dispatch({
              type: "LOG-OUT",
              payload: null,
            });
          }}
        >
          <ExitToAppIcon fontSize="large" className={classes.authIcon} />
          <MyTypography>Đăng xuất</MyTypography>
        </Link>
      </>
    );
  };

  const renderAuth = () => {
    return (
      <>
        <Link
          to="/sign-in"
          justify="flex-end"
          className={classes.headAuthItems}
          item
          lg={6}
        >
          <AccountCircleIcon fontSize="large" className={classes.authIcon} />
          <MyTypography>Đăng Nhập</MyTypography>
        </Link>

        <Link
          to="/sign-up"
          justify="flex-end"
          className={classes.headAuthItems}
          item
          lg={6}
        >
          <AccountCircleIcon fontSize="large" className={classes.authIcon} />
          <MyTypography>Đăng Ký</MyTypography>
        </Link>
      </>
    );
  };

  return currentUser.email ? renderUser() : renderAuth();
}

export default TopBarAuth;
