import { Container, Grid, makeStyles, Typography } from "@material-ui/core";
import React, { useEffect, useState } from "react";
import axios from "axios";

const useStyles = makeStyles((theme) => ({
  root: {},
  col: {
    marginBottom: 20,
  },
  thumbnail: {},
  thumbnailImg: {
    height: "100%",
    width: "100%",
    borderRadius: "4px",
    transition: "all .2s",
  },
  titleLink: {
    textDecoration: "none",
    fontSize: 24,
    color: theme.palette.common.black,
    transition: "all .2s",
    fontWeight: 600,
    "&:hover": {
      color: theme.palette.primary.main,
    },
  },
  title: {
    display: "-webkit-box",
    WebkitLineClamp: 2,
    WebkitBoxOrient: "vertical",
    overflow: "hidden",
    textOverflow: "ellipsis",
    lineHeight: 20,
    height: 42,
    fontSize: 16,
    marginBottom: 5,
    transition: "color .2s",
  },
}));

export default function News() {
  const classes = useStyles();
  const [news, setNews] = useState(null);

  useEffect(() => {
    axios({
      url: `https://60b9f19280400f00177b744b.mockapi.io/Articles01`,
      method: "GET",
    })
      .then((result) => [setNews(result.data)])
      .catch((error) => {
        console.log(error);
      });
  }, []);

  console.log("news", news);

  return (
    <Container component="main" maxWidth="md">
      {news && (
        <Grid container>
          <Grid md={6}>
            <div className={classes.thumbnail}>
              <a href={news[0].url}>
                <img
                  className={classes.thumbnailImg}
                  alt="poster"
                  src={news[0].img}
                />
              </a>
            </div>
            <a className={classes.titleLink} href={news[0].url}>
              <Typography>{news[0].title}</Typography>
            </a>
            <Typography>{news[0].text}</Typography>
          </Grid>
          <Grid md={6}></Grid>
        </Grid>
      )}
    </Container>
  );
}
