import * as actionTypes from "./constant";

const initialStateAddUser = {
  loading: false,
  data: null,
  error: null,
};

const initialStateUpdateUser = {
  loading: false,
  data: null,
  error: null,
};

export const addUserReducer = (
  state = initialStateAddUser,
  { type, payload, ...action }
) => {
  switch (type) {
    case actionTypes.ADD_USER_REQUEST:
      state.loading = true;
      state.data = null;
      state.error = null;
      return { ...state };
    case actionTypes.ADD_USER_SUCCESS:
      state.loading = false;
      state.data = payload;
      state.error = null;
      return { ...state };
    case actionTypes.ADD_USER_FAILED:
      state.loading = false;
      state.data = null;
      state.error = payload;
      return { ...state };
    default:
      return { ...state };
  }
};

export const updateUserReducer = (
  state = initialStateUpdateUser,
  { type, payload, ...action }
) => {
  switch (type) {
    case actionTypes.UPDATE_USER_REQUEST:
      state.loading = true;
      state.data = null;
      state.error = null;
      return { ...state };
    case actionTypes.UPDATE_USER_SUCCESS:
      state.loading = false;
      state.data = payload;
      state.error = null;
      return { ...state };
    case actionTypes.UPDATE_USER_FAILED:
      state.loading = false;
      state.data = null;
      state.error = payload;
      return { ...state };
    default:
      return { ...state };
  }
};
