import {
  Grid,
  Button,
  Paper,
  InputBase,
  IconButton,
  makeStyles,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  CssBaseline,
  TableBody,
} from "@material-ui/core";
import React, { useEffect, useState } from "react";
import SearchIcon from "@material-ui/icons/Search";
import { useDispatch, useSelector } from "react-redux";
import { actGetMoviePagingApi } from "./modules/action";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  rootAlert: {
    width: "100%",
    "& > * + *": {
      marginTop: theme.spacing(2),
    },
  },
  rootPagination: {
    "& > * + *": {
      marginTop: theme.spacing(2),
    },
    display: "flex",
    justifyContent: "flex-end",
    padding: theme.spacing(3),
  },
  table: {
    minWidth: 650,
  },
  paper: {
    padding: "2px 4px",
    display: "flex",
    alignItems: "center",
    width: "500px",
  },
  inputBase: {
    marginLeft: theme.spacing(1),
    flex: 1,
  },
  searchForm: {
    display: "flex",
    padding: theme.spacing(2),
    paddingBottom: theme.spacing(3),
    backgroundColor: theme.palette.common.white,
    marginTop: theme.spacing(3),
  },
  userForm: {
    marginTop: theme.spacing(3),
  },
  button: {
    margin: theme.spacing(0, 1),
  },
  buttonContainer: {
    display: "flex",
    justifyContent: "flex-end",
  },
}));

export default function ManageMovie() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const moviePaging = useSelector((state) => state.moviePagingReducers);
  const [page, setPage] = useState(1);
  const [search, setSearch] = useState("");
  const [openDialog, setOpenDialog] = useState(false);
  const numberElementOfPage = "8";

  useEffect(() => {
    dispatch(actGetMoviePagingApi(page.toString(), numberElementOfPage));
  }, []);

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const renderMoviePaging = () => {
    return moviePaging.data.items.map((movie) => {
      return (
        <TableRow key={movie.maPhim}>
          <TableCell component="th" scope="row">
            {movie.tenphim}
          </TableCell>
          <TableCell align="left">{movie.moTa}</TableCell>
          <TableCell align="left">{movie.ngayKhoiChieu}</TableCell>
          <TableCell align="left">{movie.danhGia}</TableCell>
          <TableCell align="left">{movie.hinhAnh}</TableCell>
          <TableCell align="center">
            <IconButton aria-label="edit" color="secondary">
              <EditIcon />
            </IconButton>
            <IconButton aria-label="delete" color="primary">
              <DeleteIcon />
            </IconButton>
          </TableCell>
        </TableRow>
      );
    });
  };

  return (
    <div className={classes.root}>
      <CssBaseline />
      <Grid container>
        <Grid item xs={12} className={classes.buttonContainer}>
          <Button
            variant="contained"
            color="primary"
            className={classes.button}
          >
            Thêm
          </Button>
        </Grid>
        <Grid className={classes.searchForm} item xs={12}>
          <Paper className={classes.paper} elevation={1} component="form">
            <InputBase
              className={classes.inputBase}
              placeholder="Tìm kiếm phim"
            />
            <IconButton>
              <SearchIcon color="primary" />
            </IconButton>
          </Paper>
        </Grid>
      </Grid>

      <TableContainer className={classes.userForm} component={Paper}>
        <Table className={classes.table}>
          <TableHead>
            <TableRow>
              <TableCell align="left">Tên phim</TableCell>
              <TableCell align="left">Mô tả</TableCell>
              <TableCell align="left">Ngày chiếu</TableCell>
              <TableCell align="left">Đánh giá</TableCell>
              <TableCell align="left">Hình ảnh</TableCell>
              <TableCell align="left">Chức năng</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>{moviePaging.data && renderMoviePaging()}</TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
