import {
  Container,
  CssBaseline,
  Grid,
  makeStyles,
  TextField,
  Typography,
  Button,
} from "@material-ui/core";
import React, { useState } from "react";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/date-fns";
import moment from "moment";
import { useDispatch } from "react-redux";
import { actAddMovieApi } from "../modules/action";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  formContainer: {
    backgroundColor: theme.palette.common.white,
    borderRadius: "4px",
  },
  dateTimeContainer: {
    display: "flex",
    justifyContent: "space-around",
  },
}));

export default function ManageMovieDialog() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const [selectedDate, setSelectedDate] = useState(new Date("2021-08-29"));
  const [selectedImage, setSelectedImage] = useState(
    "https://tix.vn/app/assets/img/default-film.webp"
  );
  const [selectedFile, setSelectedFile] = useState(null);

  const [movie, setMovie] = useState({
    tenPhim: "",
    biDanh: "",
    trailer: "",
    hinhAnh: "",
    moTa: "",
    maNhom: "GP09",
    ngayKhoiChieu: moment(selectedDate).format("DD-MM-YYYY"),
    danhGia: "",
  });

  const fileSelectedHandler = (event) => {
    setSelectedFile(event.target.files[0]);
    setSelectedImage(URL.createObjectURL(event.target.files[0]));

    setMovie({
      ...movie,
      ["hinhAnh"]: event.target.files[0].name,
    });
  };

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setMovie({
      ...movie,
      [name]: value,
    });
  };

  const handleDateChange = (date) => {
    setMovie({
      ...movie,
      ["ngayKhoiChieu"]: moment(date).format("DD-MM-YYYY"),
    });
    setSelectedDate(date);
  };

  const handleAddMovie = () => {
    dispatch(actAddMovieApi(movie, selectedFile));
  };

  return (
    <div className={classes.root}>
      <Container component="main" maxWidth="sm">
        <CssBaseline />
        <form className={classes.form} noValidate>
          <Grid className={classes.formContainer} container spacing={3}>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="tenPhim"
                label="Tên phim"
                name="tenPhim"
                autoComplete="tenPhim"
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="biDanh"
                label="Bí danh"
                name="biDanh"
                autoComplete="biDanh"
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="trailer"
                label="Trailer"
                name="trailer"
                autoComplete="trailer"
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <Typography>Hinh Anh</Typography>
              <img
                alt="upload image"
                style={{
                  display: "block",
                  height: 100,
                  width: 100,
                }}
                src={selectedImage}
              />
              <input
                accept="image/*"
                style={{
                  display: "none",
                }}
                id="contained-button-file"
                multiple
                type="file"
                onChange={fileSelectedHandler}
              />
              <label htmlFor="contained-button-file">
                <Button variant="contained" color="primary" component="span">
                  Chọn ảnh
                </Button>
              </label>
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="moTa"
                label="Mô tả"
                name="moTa"
                autoComplete="moTa"
                onChange={handleChange}
              />
            </Grid>

            <Grid item xs={12} className={classes.dateTimeContainer}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker
                  format="MM/dd/yyyy"
                  margin="normal"
                  id="date-picker-inline"
                  label="Date picker inline"
                  value={selectedDate}
                  onChange={handleDateChange}
                  KeyboardButtonProps={{
                    "aria-label": "change date",
                  }}
                />
              </MuiPickersUtilsProvider>
            </Grid>

            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="danhGia"
                label="Đánh giá"
                name="danhGia"
                autoComplete="danhGia"
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <Button
                variant="contained"
                color="primary"
                onClick={handleAddMovie}
              >
                Thêm
              </Button>
            </Grid>
          </Grid>
        </form>
      </Container>
    </div>
  );
}
