import {
  Box,
  Button,
  Container,
  Grid,
  makeStyles,
  Typography,
} from "@material-ui/core";
import React, { useEffect, useState } from "react";
import axios from "axios";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";

const useStyles = makeStyles((theme) => ({
  root: {},
  col: {
    marginBottom: 20,
  },
  thumbnail: {
    marginBottom: theme.spacing(1),
  },
  thumbnailImg: {
    height: "100%",
    width: "100%",
    borderRadius: "4px",
    transition: "all .2s",
  },
  titleLink: {
    textDecoration: "none",
    fontSize: 24,
    color: theme.palette.common.black,
    transition: "all .2s",

    "&:hover": {
      color: theme.palette.primary.main,
    },
  },
  title: {
    display: "-webkit-box",
    WebkitLineClamp: "2",
    WebkitBoxOrient: "vertical",
    textOverflow: "ellipsis",
    overflow: "hidden",
    lineHeight: "20px",
    height: "42px",
    fontSize: "16px",
    marginBottom: "5px",
    fontWeight: 500,
  },
  desc: {
    display: "-webkit-box",
    WebkitLineClamp: "3",
    WebkitBoxOrient: "vertical",
    textOverflow: "ellipsis",
    overflow: "hidden",

    textAlign: "justify",
    fontSize: "13px",
    color: "#4a4a4a",
    paddingRight: "5px",
    lineHeight: "19px",

    height: "55px",
    marginBottom: theme.spacing(2),
  },
  thumbnailSmall: {
    height: "50px",
    width: "50px",
    float: "left",
    marginRight: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
  titleLinkSmall: {
    textDecoration: "none",
    fontSize: 20,
    color: "#4e4e4e",
    transition: "all .2s",

    "&:hover": {
      color: theme.palette.primary.main,
    },
  },
  titleSmall: {
    display: "-webkit-box",
    WebkitLineClamp: "2",
    WebkitBoxOrient: "vertical",
    textOverflow: "ellipsis",
    overflow: "hidden",
    lineHeight: "20px",
    height: "42px",
    fontSize: "16px",
    marginBottom: "5px",
  },
  wrapButtonSeeMoreNews: {
    marginBottom: theme.spacing(4),
    width: "100%",
    display: "flex",
    justifyContent: "center",
  },
  buttonSeeMoreNEws: {
    fontSize: "14px",
    color: "#949494",
    border: "1px solid #949494",
    borderRadius: "4px",
    padding: theme.spacing(1, 3),
    transition: "all .2s",
    marginTop: theme.spacing(4),
    marginBottom: "35px",
    display: "block",
    "&:hover": {
      background: theme.palette.primary.main,
      borderColor: theme.palette.primary.main,
      color: theme.palette.common.white,
    },
  },
}));

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <div>{children}</div>}
      {/* <Box p={3}>
          <Typography>{children}</Typography>
        </Box> */}
    </div>
  );
}

export default function News() {
  const classes = useStyles();
  const [news, setNews] = useState(null);
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  useEffect(() => {
    axios({
      url: `https://60b9f19280400f00177b744b.mockapi.io/Articles01`,
      method: "GET",
    })
      .then((result) => [setNews(result.data)])
      .catch((error) => {
        console.log(error);
      });
  }, []);

  console.log("news", news);

  return (
    <Container component="div" maxWidth="md">
      <Tabs
        value={value}
        onChange={handleChange}
        aria-label="simple tabs example"
      >
        <Tab label="Item One" />
        <Tab label="Item Two" />
        <Tab label="Item Three" />
      </Tabs>
      <TabPanel value={value} index={0}>
        Item One
      </TabPanel>
      <TabPanel value={value} index={1}>
        Item Two
      </TabPanel>
      <TabPanel value={value} index={2}>
        Item Three
      </TabPanel>

      <div className={classes.wrapButtonSeeMoreNews}>
        <Button className={classes.buttonSeeMoreNEws} variant="outlined">
          XEM THÊM
        </Button>
      </div>
    </Container>
  );
}
