import {
  Button,
  Container,
  Grid,
  makeStyles,
  Typography,
} from "@material-ui/core";
import React from "react";
import backgroundApp from "../../assets/backapp.jpg";
const useStyles = makeStyles((theme) => ({
  root: {
    padding: "120px 0 80px 0",
    background: `url(${backgroundApp})`,
    backgroundSize: "contain",
  },
  left: {
    paddingTop: "60px !important",
  },
  textLarge: {
    color: theme.palette.common.white,
    fontWeight: 700,
    fontSize: "xx-large",
    width: "100%",
    marginBottom: theme.spacing(1),
  },
  textSmall: {
    color: theme.palette.common.white,
    fontSize: "medium",
    width: "100%",
    marginBottom: theme.spacing(1),
    marginTop: theme.spacing(2),
  },
  textUnder: {
    color: theme.palette.common.white,
    fontSize: "medium",
    width: "100%",
    paddingTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
  textApp: {
    color: theme.palette.common.white,
  },
  textButton: {
    backgroundColor: theme.palette.primary.main,
    padding: theme.spacing(1, 3),
    color: theme.palette.common.white,
    fontSize: "medium",
    fontWeight: theme.typography.fontWeightBold,
    border: "none",
    borderRadius: "4px",
    marginTop: theme.spacing(2),
  },
}));

function AppHome() {
  const classes = useStyles();
  return (
    <div className={classes.root}>
      <Container maxWidth="md">
        <Grid container spacing={4}>
          <Grid className={classes.left} container item xs={6}>
            <Typography className={classes.textLarge} display="block">
              Ứng dụng tiện lợi dành cho
            </Typography>
            <Typography className={classes.textLarge} display="block">
              người yêu điện ảnh
            </Typography>

            <Typography className={classes.textSmall} display="block">
              Không chỉ đặt vé, bạn còn có thể bình luận phim, chấm điểm rạp và
              đổi quà hấp dẫn.
            </Typography>

            <Button
              className={classes.textButton}
              target="_blank"
              href="https://apps.apple.com/us/app/123phim-mua-ve-lien-tay-chon/id615186197"
              variant="contained"
              color="primary"
            >
              App miễn phí - Tải về ngay!
            </Button>
            <Typography className={classes.textUnder} display="block">
              TIX có hai phiên bản{" "}
              <a
                className={classes.textApp}
                target="_blank"
                href="https://apps.apple.com/us/app/123phim-mua-ve-lien-tay-chon/id615186197"
              >
                IOS
              </a>{" "}
              &{" "}
              <a
                className={classes.textApp}
                target="_blank"
                href="https://play.google.com/store/apps/details?id=vn.com.vng.phim123"
              >
                Android
              </a>
            </Typography>
          </Grid>
          <Grid className={classes.right} item xs={6}>
            <img></img>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default AppHome;
