import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { actGetCinemaListApi } from "./modules/action";
import { makeStyles } from "@material-ui/core/styles";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import Container from "@material-ui/core/Container";
import Avatar from "@material-ui/core/Avatar";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`vertical-tabpanel-${index}`}
      aria-labelledby={`vertical-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
    display: "flex",
  },
  tabs: {
    borderRight: `1px solid ${theme.palette.divider}`,
    border: "1px solid #ebebec",
  },
}));

const renderCinemaLogo = (arrData) => {
  return arrData.map((item, index) => {
    return (
      <Tab
        style={{ minWidth: "unset", padding: 0 }}
        key={item.biDanh}
        icon={
          <Avatar
            alt={item.biDanh}
            src={item.logo}
            style={{ width: 50, height: 50, padding: 20 }}
          />
        }
      />
    );
  });
};

function CinemaList() {
  const dispatch = useDispatch();
  const cinemaList = useSelector((state) => state.cinemaListReducer);

  useEffect(() => {
    dispatch(actGetCinemaListApi());
  }, []);

  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Container maxWidth="md" className={classes.root}>
      <Tabs
        orientation="vertical"
        value={value}
        onChange={handleChange}
        className={classes.tabs}
      >
        {cinemaList.data ? renderCinemaLogo(cinemaList.data) : null}
      </Tabs>
      <TabPanel value={value} index={0}>
        Item One
      </TabPanel>
      <TabPanel value={value} index={1}>
        Item Two
      </TabPanel>
      <TabPanel value={value} index={2}>
        Item Three
      </TabPanel>
      <TabPanel value={value} index={3}>
        Item Four
      </TabPanel>
      <TabPanel value={value} index={4}>
        Item Five
      </TabPanel>
      <TabPanel value={value} index={5}>
        Item Six
      </TabPanel>
    </Container>
  );
}

export default CinemaList;
