import {
  Avatar,
  Button,
  Checkbox,
  Container,
  CssBaseline,
  FormControlLabel,
  Grid,
  TextField,
  Typography,
} from "@material-ui/core";
import { useForm } from "react-hook-form";
import { useStyles } from "./style";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { useDispatch, useSelector } from "react-redux";
import { actSignInApi } from "./modules/action";
import { Link, useHistory } from "react-router-dom";
import Alert from "@material-ui/lab/Alert";

const schema = yup.object().shape({
  email: yup.string().required("User name is a required field !"),
  password: yup
    .string()
    .required("Password is a required field !")
    .min(6, "Password must be at least 6 characters"),
});

const SignIn = () => {
  const classes = useStyles();
  const dispatch = useDispatch();
  const history = useHistory();
  const signInReducer = useSelector((state) => state.signInReducer);
  const { register, handleSubmit, errors } = useForm({
    mode: "onBlur",
    resolver: yupResolver(schema),
  });

  const onSubmit = (data) => {
    dispatch(actSignInApi(data, history));
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}></Avatar>
        <Typography component="h1" variant="h5">
          Sign in
        </Typography>
        <form
          className={classes.form}
          noValidate
          onSubmit={handleSubmit(onSubmit)}
        >
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            autoComplete="email"
            // autoFocus
            inputRef={register}
            error={!!errors.email}
            helperText={errors?.email?.message}
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
            inputRef={register}
            error={!!errors.password}
            helperText={errors?.password?.message}
          />
          <FormControlLabel
            control={
              <Checkbox
                name="remember"
                color="primary"
                // inputRef={register}
                defaultValue={false}
              />
            }
            label="Nhớ tài khoản"
          />

          {/* In ra loi neu dang nhap that bai */}
          {signInReducer.error ? (
            <Alert severity="error">
              {signInReducer.error.response.data.error}
            </Alert>
          ) : null}

          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Đăng nhập
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Quên mật khẩu ?
              </Link>
            </Grid>
            <Grid item>
              <Link to="/sign-up" variant="body2">
                {"Bạn chưa có tài khoản? Đăng ký"}
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
    </Container>
  );
};

export default SignIn;
