import * as actionTypes from "./constant";
import axios from "axios";

export const actGetUserPagingApi = (soTrang, soPhanTuTrenTrang, tuKhoa) => {
  return (dispatch) => {
    dispatch(actGetUserPagingRequest());
    axios({
      url: `https://movie0706.cybersoft.edu.vn/api/QuanLyNguoiDung/TimKiemNguoiDungPhanTrang`,
      method: "GET",
      params: {
        MaNhom: "GP09",
        soTrang,
        soPhanTuTrenTrang,
        tuKhoa,
      },
    })
      .then((result) => {
        dispatch(actGetUserPagingSuccess(result.data));
      })
      .catch((error) => {
        dispatch(actGetUserPagingFailed(error));
      });
  };
};

export const actDeleteUserApi = (taiKhoan) => {
  let accessToken = "";
  if (localStorage.getItem("currentUser")) {
    accessToken = JSON.parse(localStorage.getItem("currentUser")).accessToken;
  }
  return (dispatch) => {
    dispatch(actDeleteUserRequest());
    axios({
      url: `https://movie0706.cybersoft.edu.vn/api/QuanLyNguoiDung/XoaNguoiDung`,
      method: "DELETE",
      params: {
        taiKhoan,
      },
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    })
      .then((result) => {
        dispatch(actDeleteUserSuccess(result.data));
      })
      .catch((error) => {
        dispatch(actDeleteUserFailed(error));
      });
  };
};

const actGetUserPagingRequest = () => {
  return {
    type: actionTypes.GET_USER_PAGING_REQUEST,
  };
};

const actGetUserPagingSuccess = (userPaging) => {
  return {
    type: actionTypes.GET_USER_PAGING_SUCCESS,
    payload: userPaging,
  };
};

const actGetUserPagingFailed = (error) => {
  return {
    type: actionTypes.GET_USER_PAGING_SUCCESS,
    payload: error,
  };
};

const actDeleteUserRequest = () => {
  return {
    type: actionTypes.DELETE_USER_REQUEST,
  };
};

const actDeleteUserSuccess = (user) => {
  return {
    type: actionTypes.DELETE_USER_SUCCESS,
    payload: user,
  };
};

const actDeleteUserFailed = (error) => {
  return {
    type: actionTypes.DELETE_USER_FAILED,
    payload: error,
  };
};
