import * as actionTypes from "./constant";
import axios from "axios";

export const actAddMovieApi = (movie, selectedFile) => {
  const accessToken = JSON.parse(
    localStorage.getItem("currentUser")
  ).accessToken;
  return (dispatch) => {
    dispatch(actAddMovieRequest());
    axios({
      url: `https://movie0706.cybersoft.edu.vn/api/QuanLyPhim/ThemPhim`,
      method: "POST",
      data: movie,
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    })
      .then((result) => {
        dispatch(actAddMovieSuccess(result.data));

        // var frm = new FormData();
        // frm.append("File", File, movie.hinhAnh);
        // frm.append("tenPhim", movie.tenPhim);
        // frm.append("maNhom", "GP09");

