import {
  Button,
  Container,
  Divider,
  FormControl,
  Grid,
  makeStyles,
  NativeSelect,
} from "@material-ui/core";
import axios from "axios";
import { setDayOfYear } from "date-fns";
import React, { useState } from "react";
import { useSelector } from "react-redux";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "block",
    height: theme.spacing(10),
    position: "absolute",
    width: "100%",
    left: "50%",
    bottom: "0",
    transform: "translate(-50%,50%)",
  },
  homeToolContainer: {
    backgroundColor: theme.palette.common.white,
    borderRadius: "5px",
    zIndex: "2",
    boxShadow: "0 0 10px rgb(0 0 0 / 30%)",
    height: "100%",
  },
  nativeSelect: {
    "&:before": {
      content: "unset",
    },

    display: "flex",
    justifyContent: "space-around",
    height: "100%",

    fontSize: "16px",
    fontWeight: 500,
    padding: "20px",
  },
  gridItem: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-around",
  },
  selectFilm: {
    flexGrow: "1",
    height: "100%",
  },
  selectCinema: {
    flexGrow: "1",
    height: "100%",
  },
  selectDate: {
    flexGrow: "1",
    height: "100%",
  },
  selectSession: {
    flexGrow: "1",
    height: "100%",
  },
  buyContainer: {
    flexGrow: "1",
    height: "100%",
  },
  formControl: {
    width: "100%",
    height: "100%",
    justifyContent: "center",
    position: "relative",
    "&:after": {
      position: "absolute",
      display: "block",
      content: "''",
      height: "60%",
      width: "1px",
      backgroundColor: theme.palette.grey[200],
    },
  },
  divider: {
    position: "relative",
    height: "60%",

    top: 0,
    transform: "translateY(30%)",
    opacity: "0.6",
  },
  button: {
    padding: "10px",
    margin: "0 10px",
  },
}));

export default function HomeTool() {
  const classes = useStyles();

  //   MÃ£
  const [film, setFilm] = useState("");
  const [cinema, setCinema] = useState("");
  const [date, setDate] = useState("");
  const [session, setSession] = useState("");

  const [cinemaByMovie, setCinemaByMovie] = useState(null);

  const movieList = useSelector((state) => state.movieListReducer);

  const handleChangeFilm = (event) => {
    setFilm(event.target.value);

    axios({
      url: `https://movie0706.cybersoft.edu.vn/api/QuanLyRap/LayThongTinLichChieuPhim`,
      method: "GET",
      params: {
        MaPhim: event.target.value,
      },
    })
      .then((result) => {
        console.log("result", result.data.heThongRapChieu);
        setCinemaByMovie(result.data.heThongRapChieu);
      })
      .catch((error) => {
        setCinemaByMovie(error);
      });
  };
  const handleChangeCinema = (event) => {
    setCinema(event.target.value);
  };
  const handleChangeDate = (event) => {
    setDate(event.target.value);
  };
  const handleChangeSession = (event) => {
    setSession(event.target.value);
  };

  const renderFilm = (arr) => {
    return arr.map((item) => {
      return (
        <option key={item.maPhim} value={item.maPhim}>
          {item.tenPhim}
        </option>
      );
    });
  };

  const renderCinema = (arr) => {
    return arr.map((item, index) => {
      item.forEach((childItem, childIndex) => {
        return (
          <option key={item.maCumRap} value={item.maCumRap}>
            {item.tenCumRap}
          </option>
        );
      });
    });
  };

  return (
    <Container id="homeTool" className={classes.root} maxWidth="md">
      <Grid container className={classes.homeToolContainer}>
        <Grid item xs={4} className={classes.gridItem}>
          <div className={classes.selectFilm}>
            <FormControl className={classes.formControl}>
              <NativeSelect
                value={film}
                name="film"
                onChange={handleChangeFilm}
                className={classes.nativeSelect}
              >
                <option value="" disabled>
                  Phim
                </option>
                {movieList.data && renderFilm(movieList.data)}
              </NativeSelect>
            </FormControl>
          </div>
        </Grid>

        <Grid item xs={2}>
          <div className={classes.selectCinema}>
            <FormControl className={classes.formControl}>
              <NativeSelect
                value={cinema}
                name="cinema"
                onChange={handleChangeCinema}
                className={classes.nativeSelect}
              >
                <option value="" disabled>
                  Ráº¡p
                </option>
                {cinemaByMovie && renderCinema(cinemaByMovie)}
              </NativeSelect>
            </FormControl>
          </div>
        </Grid>

        <Grid item xs={2}>
          <div className={classes.selectDate}>
            <FormControl className={classes.formControl}>
              <NativeSelect
                value={date}
                name="date"
                onChange={handleChangeDate}
                className={classes.nativeSelect}
              >
                <option value="" disabled>
                  NgÃ y chiáº¿u
                </option>
              </NativeSelect>
            </FormControl>
          </div>
        </Grid>

        <Grid item xs={2}>
          <div className={classes.selectSession}>
            <FormControl className={classes.formControl}>
              <NativeSelect
                value={session}
                name="session"
                onChange={handleChangeSession}
                className={classes.nativeSelect}
              >
                <option value="" disabled>
                  Suáº¥t chiáº¿u
                </option>
              </NativeSelect>
            </FormControl>
          </div>
        </Grid>

        <Grid item xs={2}>
          <div className={classes.buyContainer}>
            <FormControl className={classes.formControl}>
              <Button
                className={classes.button}
                variant="contained"
                color="primary"
              >
                MUA VÃ NGAY
              </Button>
            </FormControl>
          </div>
        </Grid>
      </Grid>
    </Container>
  );
}
