import React, { useState } from "react";
import { Typography } from "@material-ui/core";
import PlayCircleOutlineOutlinedIcon from "@material-ui/icons/PlayCircleOutlineOutlined";
import Fab from "@material-ui/core/Fab";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  thumbnail: {
    position: "relative",
    borderRadius: "4px",
    backgroundPosition: "center center",
    backgroundSize: "100% auto",
    backgroundRepeat: "no-repeat",
    display: "block",
    marginBottom: "10px",
    height: "400px",
    "&:hover $showHover": {
      opacity: "1",
      visibility: "visible",
      transition: "0.3s ease",
    },
  },
  showHover: {
    position: "absolute",
    width: "100%",
    height: "100%",
    top: "0",
    borderRadius: "4px",
    background: "linear-gradient(to top,#000,transparent 100%)",
    opacity: "0",
    visibility: "hidden",
  },
  moTa: {
    display: "-webkit-box",
    WebkitLineClamp: "2",
    WebkitBoxOrient: "vertical",

    textOverflow: "ellipsis",
    // whiteSpace: "nowrap",

    overflow: "hidden",
  },
}));

function MovieSingle(props) {
  const classes = useStyles();

  return (
    <div>
      {/* Thumbnail */}
      <a>
        <div
          className={classes.thumbnail}
          style={{
            backgroundImage: `url(${props.data.hinhAnh})`,
          }}
        >
          {/* Thumbnail when hover */}
          <div className={classes.showHover}>
            <Fab
              aria-label="add"
              style={{
                position: "absolute",
                top: "50%",
                left: "50%",
                transform: "translate(-50%,-50%)",
                zIndex: "3",
                border: "none",
                background: "0 0",
              }}
              onClick={() => {}}
            >
              <PlayCircleOutlineOutlinedIcon
                style={{ color: "#fff", fontSize: "56px" }}
              />
            </Fab>
          </div>
        </div>
        {/* Information */}
        <div>
          <div>
            <Typography variant="h2">{props.data.tenPhim}</Typography>
          </div>
          <div>
            <Typography className={classes.moTa} variant="h4">
              {props.data.moTa}
            </Typography>
          </div>
        </div>
      </a>
    </div>
  );
}

export default MovieSingle;
