import {
  Button,
  Container,
  Grid,
  makeStyles,
  Typography,
} from "@material-ui/core";
import React from "react";
import Carousel from "react-material-ui-carousel";
import backgroundApp from "../../assets/backapp.jpg";
import mobile from "../../assets/mobile.png";
import bannerSlider1 from "../../assets/AppHome/banner-slider-1.jpg";
import bannerSlider2 from "../../assets/AppHome/banner-slider-2.jpg";
import bannerSlider3 from "../../assets/AppHome/banner-slider-3.jpg";
import bannerSlider4 from "../../assets/AppHome/banner-slider-4.jpg";
import bannerSlider5 from "../../assets/AppHome/banner-slider-5.jpg";
import bannerSlider6 from "../../assets/AppHome/banner-slider-6.jpg";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: "120px 0 80px 0",
    background: `url(${backgroundApp})`,
    backgroundSize: "contain",
    [theme.breakpoints.down("sm")]: {
      paddingTop: 0,
    },
  },
  left: {
    paddingTop: "60px !important",
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
      justifyContent: "center",
      alignItems: "center",
    },
  },
  textLarge: {
    color: theme.palette.common.white,
    fontWeight: 700,
    fontSize: "xx-large",
    width: "100%",
    marginBottom: theme.spacing(1),
    [theme.breakpoints.down("sm")]: {
      width: "unset",
    },
    [theme.breakpoints.down("xs")]: {
      fontSize: theme.typography.h1.fontSize,
    },
  },
  textSmall: {
    color: theme.palette.common.white,
    fontSize: "medium",
    width: "100%",
    marginBottom: theme.spacing(1),
    marginTop: theme.spacing(2),
    [theme.breakpoints.down("sm")]: {
      width: "unset",
      textAlign: "center",
    },
  },
  textUnder: {
    color: theme.palette.common.white,
    fontSize: "medium",
    width: "100%",
    paddingTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
    [theme.breakpoints.down("sm")]: {
      width: "unset",
    },
  },
  textApp: {
    color: theme.palette.common.white,
  },
  textButton: {
    backgroundColor: theme.palette.primary.main,
    padding: theme.spacing(1, 3),
    color: theme.palette.common.white,
    fontSize: "medium",
    fontWeight: theme.typography.fontWeightBold,
    border: "none",
    borderRadius: "4px",
    marginTop: theme.spacing(2),
  },

  right: {
    position: "relative",
    padding: "0 !important",
  },
  imgPhone: {
    position: "relative",
    padding: "0 28%",
    display: "block",
    maxWidth: "100%",
    height: "auto",
  },
  carousel: {
    display: "block",
    position: "absolute",
    padding: "1.5% 29.2% 0 29.2%",
    top: 0,
    left: 0,
    width: "100%",
    borderRadius: "20px",
  },
  rightItem: {
    position: "relative",
    display: "block",
    overflow: "hidden",
    margin: 0,
    padding: 0,
    borderRadius: "20px",
  },
}));
const data = [
  bannerSlider1,
  bannerSlider2,
  bannerSlider3,
  bannerSlider4,
  bannerSlider5,
  bannerSlider6,
];
function AppHome() {
  const classes = useStyles();

  const renderPhone = () => {
    return data.map((item, index) => {
      return (
        <div key={index} className={classes.rightItem}>
          <img
            style={{
              display: "block",
              width: "100%",
              border: 0,
              verticalAlign: "middle",
              overflow: "hidden",
            }}
            src={item}
            alt="slider"
          ></img>
        </div>
      );
    });
  };

  return (
    <div className={classes.root}>
      <Container maxWidth="md">
        <Grid container spacing={4}>
          <Grid className={classes.left} container item xs={12} md={6}>
            <Typography className={classes.textLarge} display="block">
              Ứng dụng tiện lợi dành cho
            </Typography>
            <Typography className={classes.textLarge} display="block">
              người yêu điện ảnh
            </Typography>

            <Typography className={classes.textSmall} display="block">
              Không chỉ đặt vé, bạn còn có thể bình luận phim, chấm điểm rạp và
              đổi quà hấp dẫn.
            </Typography>

            <Button
              className={classes.textButton}
              target="_blank"
              href="https://apps.apple.com/us/app/123phim-mua-ve-lien-tay-chon/id615186197"
              variant="contained"
              color="primary"
            >
              App miễn phí - Tải về ngay!
            </Button>
            <Typography className={classes.textUnder} display="block">
              TIX có hai phiên bản{" "}
              <a
                className={classes.textApp}
                target="_blank"
                href="https://apps.apple.com/us/app/123phim-mua-ve-lien-tay-chon/id615186197"
              >
                IOS
              </a>{" "}
              &{" "}
              <a
                className={classes.textApp}
                target="_blank"
                href="https://play.google.com/store/apps/details?id=vn.com.vng.phim123"
              >
                Android
              </a>
            </Typography>
          </Grid>
          <Grid className={classes.right} item xs={12} md={6}>
            <img className={classes.imgPhone} alt="phone" src={mobile}></img>
            <Carousel
              className={classes.carousel}
              animation="fade"
              indicators={false}
              navButtonsAlwaysInvisible={true}
              autoPlay={true}
              timeout={200}
            >
              {renderPhone()}
            </Carousel>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default AppHome;
