import React, { useState } from "react";
import { Typography } from "@material-ui/core";
import PlayCircleOutlineOutlinedIcon from "@material-ui/icons/PlayCircleOutlineOutlined";
import Fab from "@material-ui/core/Fab";
import { makeStyles } from "@material-ui/core/styles";
import { Link } from "react-router-dom";
const useStyles = makeStyles((theme) => ({
  film: {
    "&:hover $hoverThumbnail": {
      opacity: "1",
      visibility: "visible",
    },
    "&:hover $info": {
      opacity: "1",
      visible: "visibility",
    },
  },
  thumbnail: {
    position: "relative",
    borderRadius: "4px",
    backgroundPosition: "center center",
    backgroundSize: "100% auto",
    backgroundRepeat: "no-repeat",
    display: "block",
    marginBottom: "10px",
    height: "400px",
  },
  hoverThumbnail: {
    position: "absolute",
    width: "100%",
    height: "100%",
    top: "0",
    borderRadius: "4px",
    background: "linear-gradient(to top,#000,transparent 100%)",
    opacity: "0",
    visibility: "hidden",
    transition: "all 0.3s ",
  },
  info: {
    position: "relative",
    display: "block",
    opacity: "0",
    visible: "hidden",
    transition: "all 0.3s ",
  },
  tenPhim: {
    height: "42px",
    maxHeight: "42px",
    lineHeight: "22px",
    display: "-webkit-box",
    WebkitLineClamp: "2",
    WebkitBoxOrient: "vertical",
    textOverflow: "ellipsis",
    overflow: "hidden",
  },
  moTa: {
    display: "-webkit-box",
    WebkitLineClamp: "3",
    WebkitBoxOrient: "vertical",
    textOverflow: "ellipsis",
    overflow: "hidden",

    margin: theme.spacing(1, 0, 2, 0),
    color: theme.palette.grey[600],
  },
  muaVe: {
    position: "absolute",
    top: 0,
    left: 0,
    display: "block",
    width: "100%",
    borderRadius: "4px",
    color: theme.palette.common.white,
    fontSize: theme.typography.h2.fontSize,
    fontWeight: theme.typography.h2.fontWeight,
    textDecoration: "none",

    textAlign: "center",
    padding: theme.spacing(2, 1),
    background: "linear-gradient(to left,#fb4226,#ce3017 100%)",

    "&:hover": {
      background: theme.palette.primary.main,
    },
  },
}));

function MovieSingle(props) {
  const classes = useStyles();

  return (
    <div className={classes.film}>
      {/* Thumbnail */}
      <a>
        <div
          className={classes.thumbnail}
          style={{
            backgroundImage: `url(${props.data.hinhAnh})`,
          }}
        >
          {/* Thumbnail when hover */}
          <div className={classes.hoverThumbnail}>
            <Fab
              aria-label="add"
              style={{
                position: "absolute",
                top: "50%",
                left: "50%",
                transform: "translate(-50%,-50%)",
                zIndex: "3",
                border: "none",
                background: "0 0",
              }}
            >
              <PlayCircleOutlineOutlinedIcon
                style={{ color: "#fff", fontSize: "56px" }}
              />
            </Fab>
          </div>
        </div>
        {/* Information */}
        <div className={classes.info}>
          <div>
            <Typography className={classes.tenPhim} variant="h2">
              {props.data.tenPhim}
            </Typography>
          </div>
          <div>
            <Typography className={classes.moTa} variant="h4">
              {props.data.moTa}
            </Typography>
          </div>
          {/* Mua vé button */}
          <div>
            <Link className={classes.muaVe}>MUA VÉ</Link>
          </div>
        </div>
      </a>
    </div>
  );
}

export default MovieSingle;
