import {
  Container,
  CssBaseline,
  Grid,
  makeStyles,
  TextField,
  Typography,
  Button,
} from "@material-ui/core";
import React, { useState } from "react";
import {
  KeyboardDatePicker,
  KeyboardTimePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/date-fns";
import moment from "moment";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  formContainer: {
    backgroundColor: theme.palette.common.white,
    borderRadius: "4px",
  },
  dateTimeContainer: {
    display: "flex",
    justifyContent: "space-around",
  },
}));

export default function ManageMovie() {
  const classes = useStyles();

  const [selectedDate, setSelectedDate] = useState(new Date("2021-08-29"));
  const [imageUpload, setImageUpload] = useState(
    "https://topshare.vn/wp-content/uploads/2021/02/Hinh-anh-buon-co-don-2-edited.jpg"
  );

  const [movie, setMovie] = useState({
    tenPhim: "",
    biDanh: "",
    trailer: "",
    hinhAnh: "",
    moTa: "",
    maNhom: "GP09",
    ngayKhoiChieu: "",
    danhGia: "",
  });

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setMovie({
      ...movie,
      [name]: value,
    });
  };

  console.log(movie);

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const onImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      const img = event.target.files[0];
      setImageUpload(URL.createObjectURL(img));
    }
  };

  console.log(moment(selectedDate).format("DD-MM-YYYY"));

  return (
    <div className={classes.root}>
      <Container component="main" maxWidth="sm">
        <CssBaseline />
        <form className={classes.form} noValidate>
          <Grid className={classes.formContainer} container spacing={3}>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="tenPhim"
                label="Tên phim"
                name="tenPhim"
                autoComplete="tenPhim"
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="biDanh"
                label="Bí danh"
                name="biDanh"
                autoComplete="biDanh"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="trailer"
                label="Trailer"
                name="trailer"
                autoComplete="trailer"
              />
            </Grid>
            <Grid item xs={12}>
              {/* <Typography>Hinh Anh</Typography>
              <img
                alt="upload image"
                src={imageUpload}
                style={{
                  display: "block",
                  height: 100,
                  width: 100,
                }}
              />
              <input
                accept="image/*"
                style={{
                  display: "none",
                }}
                id="contained-button-file"
                multiple
                type="file"
                onChange={onImageChange}
              />
              <label htmlFor="contained-button-file">
                <Button variant="contained" color="primary" component="span">
                  Upload
                </Button>
              </label> */}
              <TextField
                variant="outlined"
                required
                fullWidth
                id="hinhAnh"
                label="Hình ảnh"
                name="hinhAnh"
                autoComplete="hinhAnh"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="moTa"
                label="Mô tả"
                name="moTa"
                autoComplete="moTa"
              />
            </Grid>

            <Grid item xs={12} className={classes.dateTimeContainer}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker
                  format="MM/dd/yyyy"
                  margin="normal"
                  id="date-picker-inline"
                  label="Date picker inline"
                  value={selectedDate}
                  onChange={handleDateChange}
                  KeyboardButtonProps={{
                    "aria-label": "change date",
                  }}
                />
              </MuiPickersUtilsProvider>
            </Grid>

            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="danhGia"
                label="Đánh giá"
                name="danhGia"
                autoComplete="danhGia"
              />
            </Grid>
          </Grid>
        </form>
      </Container>
    </div>
  );
}
