import {
  Button,
  Container,
  Divider,
  FormControl,
  Grid,
  makeStyles,
  NativeSelect,
  Typography,
} from "@material-ui/core";
import axios from "axios";
import React, { useState } from "react";
import { useSelector } from "react-redux";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "block",
    height: theme.spacing(10),
    position: "absolute",
    width: "100%",
    left: "50%",
    bottom: "0",
    transform: "translate(-50%,50%)",
  },
  homeToolContainer: {
    backgroundColor: theme.palette.common.white,
    borderRadius: "5px",
    zIndex: "2",
    boxShadow: "0 0 10px rgb(0 0 0 / 30%)",
    height: "100%",
  },
  nativeSelect: {
    "&:before": {
      content: "unset",
    },

    display: "flex",
    justifyContent: "space-around",
    height: "100%",

    fontSize: "16px",
    fontWeight: 500,
    padding: "20px",
  },
  gridItem: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-around",
  },
  selectFilm: {
    flexGrow: "1",
    height: "100%",
  },
  selectCinema: {
    flexGrow: "1",
    height: "100%",
  },
  selectDate: {
    flexGrow: "1",
    height: "100%",
  },
  selectSession: {
    flexGrow: "1",
    height: "100%",
  },
  buyContainer: {
    flexGrow: "1",
    height: "100%",
  },
  formControl: {
    width: "100%",
    height: "100%",
    justifyContent: "center",
    position: "relative",
    "&:after": {
      position: "absolute",
      display: "block",
      content: "''",
      height: "60%",
      width: "1px",
      backgroundColor: theme.palette.grey[200],
    },
  },
  divider: {
    position: "relative",
    height: "60%",

    top: 0,
    transform: "translateY(30%)",
    opacity: "0.6",
  },
  button: {
    padding: "10px",
    margin: "0 10px",
  },
}));

export default function HomeTool() {
  const classes = useStyles();

  const [state, setState] = useState({
    film: "",
    cinema: "",
    date: "",
    session: "",
  });
  const [cinemaByMovie, setCinemaByMovie] = useState(null);

  const movieList = useSelector((state) => state.movieListReducer);

  const handleChange = (event) => {
    const name = event.target.name;
    setState({
      ...state,
      [name]: event.target.value,
    });
  };

  const renderFilm = (arr) => {
    return arr.map((item) => {
      console.log(item);
      return (
        <option
          key={item.maPhim}
          value={item.maPhim}
          onCLick={() => {
            console.log(item);
            axios({
              url: `https://movie0706.cybersoft.edu.vn/api/QuanLyRap/LayThongTinLichChieuPhim`,
              method: "GET",
              params: {
                MaPhim: `${item.maPhim}`,
              },
            })
              .then((result) => {
                setCinemaByMovie(result.data);
              })
              .catch((error) => {
                setCinemaByMovie(error);
              });
          }}
        >
          <Typography>{item.tenPhim}</Typography>
        </option>
      );
    });
  };

  return (
    <Container id="homeTool" className={classes.root} maxWidth="md">
      <Grid container className={classes.homeToolContainer}>
        <Grid item xs={4} className={classes.gridItem}>
          <div className={classes.selectFilm}>
            <FormControl className={classes.formControl}>
              <NativeSelect
                value={state.film}
                name="film"
                onChange={handleChange}
                className={classes.nativeSelect}
              >
                <option value="" disabled>
                  Phim
                </option>
                {/* <option value={10}>Kiều</option>
                <option value={20}>Cô gái đến từ hư vô</option>
                <option value={30}>Thirty</option> */}
                {movieList.data && renderFilm(movieList.data)}
              </NativeSelect>
            </FormControl>
          </div>
        </Grid>
        {/* <Divider className={classes.divider} orientation="vertical" /> */}
        <Grid item xs={2}>
          <div className={classes.selectCinema}>
            <FormControl className={classes.formControl}>
              <NativeSelect
                value={state.cinema}
                name="cinema"
                onChange={handleChange}
                className={classes.nativeSelect}
              >
                <option value="" disabled>
                  Rạp
                </option>
              </NativeSelect>
            </FormControl>
          </div>
        </Grid>
        {/* <Divider className={classes.divider} orientation="vertical" /> */}
        <Grid item xs={2}>
          <div className={classes.selectDate}>
            <FormControl className={classes.formControl}>
              <NativeSelect
                value={state.date}
                name="date"
                onChange={handleChange}
                className={classes.nativeSelect}
              >
                <option value="" disabled>
                  Ngày chiếu
                </option>
              </NativeSelect>
            </FormControl>
          </div>
        </Grid>
        {/* <Divider className={classes.divider} orientation="vertical" /> */}
        <Grid item xs={2}>
          <div className={classes.selectSession}>
            <FormControl className={classes.formControl}>
              <NativeSelect
                value={state.session}
                name="session"
                onChange={handleChange}
                className={classes.nativeSelect}
              >
                <option value="" disabled>
                  Suất chiếu
                </option>
              </NativeSelect>
            </FormControl>
          </div>
        </Grid>
        {/* <Divider className={classes.divider} orientation="vertical" /> */}
        <Grid item xs={2}>
          <div className={classes.buyContainer}>
            <FormControl className={classes.formControl}>
              <Button
                className={classes.button}
                variant="contained"
                color="primary"
              >
                MUA VÉ NGAY
              </Button>
            </FormControl>
          </div>
        </Grid>
      </Grid>
    </Container>
  );
}
