import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import EditIcon from "@material-ui/icons/Edit";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import { useDispatch, useSelector } from "react-redux";
import { actGetUserPagingApi } from "./modules/action";
import { Pagination } from "@material-ui/lab";
import { InputBase } from "@material-ui/core";
import SearchIcon from "@material-ui/icons/Search";
const useStyles = makeStyles((theme) => ({
  root: {
    "& > * + *": {
      marginTop: theme.spacing(2),
    },
  },
  table: {
    minWidth: 650,
  },
}));

export default function ManageUser() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const userPaging = useSelector((state) => state.userPagingReducer);
  const [page, setPage] = useState(1);
  const handleChange = (event, value) => {
    setPage(value);
    dispatch(actGetUserPagingApi(value.toString(), "8"));
  };

  useEffect(() => {
    dispatch(actGetUserPagingApi("1", "8"));
  }, []);

  const renderUserPaging = () => {
    return userPaging.data.items.map((user) => {
      return (
        <TableRow key={user.email}>
          <TableCell component="th" scope="row">
            {user.taiKhoan}
          </TableCell>
          <TableCell align="right">{user.hoTen}</TableCell>
          <TableCell align="right">{user.email}</TableCell>
          <TableCell align="right">{user.soDt}</TableCell>
          <TableCell align="right">
            {user.maLoaiNguoiDung === "KhachHang" ? "Khách Hàng" : "Quản Trị"}
          </TableCell>
          <TableCell align="right">
            <IconButton aria-label="edit" color="secondary">
              <EditIcon />
            </IconButton>
            <IconButton aria-label="delete" color="primary">
              <DeleteIcon />
            </IconButton>
          </TableCell>
        </TableRow>
      );
    });
  };

  return (
    <div>
      <Paper elevation={4} component="form">
        <InputBase placeholder="Tìm kiếm phim" />
        <IconButton>
          <SearchIcon color="primary" />
        </IconButton>
      </Paper>

      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Tài Khoản</TableCell>
              <TableCell align="right">Họ Tên</TableCell>
              <TableCell align="right">Email</TableCell>
              <TableCell align="right">Số Điện Thoại</TableCell>
              <TableCell align="right">Loại Người Dùng</TableCell>
              <TableCell align="right">Chức năng</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>{userPaging.data ? renderUserPaging() : null}</TableBody>
        </Table>
      </TableContainer>
      {userPaging.data ? (
        <div className={classes.root}>
          <Pagination
            count={userPaging.data.totalPages}
            page={page}
            onChange={handleChange}
          />
        </div>
      ) : null}
    </div>
  );
}
